INSERT INTO `pms_emailtemplate` (`emailtemplate_id`, `emailtemplate_code`, `emailtemplate_desc`, `emailtemplate_subject`, `emailtemplate_body`) VALUES
(5, 'EMAIL_SETTING_VERIFICATION', 'Email Setting Verification', 'Email Verification', '<p>Hi %var_cadminusername%,</p>\n\n<p>This is test email to verify your email settings.</p>\n\n<p>- Support Team</p>\n'),
(6, 'EMAIL_FORGOT_PASS', 'Template 2', 'Forgot Password', '<p>Hi %tpl_userfname% %tpl_userlname%,</p>\n\n<p>Please find like for recover password. To recover you password please click on below link.</p>\n\n<p>%tpl_activationlink%</p>\n\n<p>Thanks,</p>\n\n<p>- Support Team</p>\n'),
(11, 'EMAIL_USER_ACTIVATION', 'User Activation Link', 'Account Activation Link', '<p>Hi %tpl_userfname% %tpl_userlname%,</p>\n\n<p>Thank you for Registrating with us. To activate you account please click on below link.</p>\n\n<p>%tpl_activationlink%</p>\n\n<p>Thanks,</p>\n\n<p>- Support Team</p>\n'),
(12, 'CONNCTIONMSG', 'Connection message', 'Connection request', '<div style="height:30px;background-color:#CCCCCC;padding-top:10px;">&nbsp;<strong> &nbsp;E<strong style="line-height: 1.6em;">connect : Connection Request</strong></strong></div>\n\n<p>Hi&nbsp;<span style="font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px;">%tpl_userfname%&nbsp;%tpl_userlname%</span>,</p>\n\n<p><span style="line-height: 1.6em;">I would like to add you on my social networl Econnect</span></p>\n\n<p><span style="line-height: 1.6em;">From</span></p>\n\n<p>%tpl_fromuserfname% %tpl_fromuserlname%</p>\n\n<p>%tpl_officename% , %tpl_countryname%</p>\n\n<p>&nbsp;</p>\n'),
(13, 'PMS_NEW_EMP_REG', 'New Employee Registration', 'Welcome to Varroc Group Online PMS', '<p>Dear <span style="line-height: 1.6em;">%tpl_fname% %tpl_lname%</span>,</p>\n\n<p>You would have received communication from your Corporate HR about the Online PMS. This mail will help you to login in PMS Portal.<br />\nThe PMS Portal (<a href="http://pms.varrocgroup.com" target="_blank">http://pms.varrocgroup.com</a>) is hosted on External Server,&nbsp; which is available to you through a authentication procedure, where, you will first enter the username &amp; password provided by us.</p>\n\n<p>The following steps needs to be taken:<br />\n<strong>Step 1:</strong> Click <a href="http://pms.varrocgroup.com" target="_blank">http://pms.varrocgroup.com</a> . The site will ask for your username &amp; password.<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; User Name: Your Varroc email address %tpl_username%<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Password: %tpl_password%<br />\n<strong>Step 2:</strong> Enter your password &amp; click on submit.<br />\n<strong>Step 3:</strong> Change the default Password to new password and re-confirm your new password of your own choice.<br />\n<strong>Step 4:</strong> Write down your username and password. This will help you re-access the PMS Portal in case of a drop in internet connection.</p>\n\n<p>In Case<br />\n-&nbsp; You forgot password, simply click Forgot Password link enter your email Id and submit , System will regenerate a link and mail it your email ID associated.<br />\n- You need any clarifications, please feel free to get in touch with your Corporate HR Team at&nbsp; pmsqueries@varrocgroup.com<br />\n&nbsp;<br />\nRegards,<br />\nCorporate HR Team<br />\nVarroc Group</p>\n');
